const router = require("express").Router();

const { response } = require("express");
let Product = require("../models/DomProduct");

//add the new Product to databse
router.route("/domproductadd").post((req, res) => {
  const domProductName = req.body.domProductName;
  const domProductCode = Number(req.body.domProductCode);
  const domProductQuentity = Number(req.body.domProductQuentity);
  const domManDate = Date(req.body.domManDate);
  const domExpireDate = Date(req.body.domExpireDate);
  const domShipingAddress = req.body.domShipingAddress;
  const domContactNumber = req.body.domContactNumber;

  const newProduct = new Product({
    domProductName,
    domProductCode,
    domProductQuentity,
    domExpireDate,
    domManDate,
    domShipingAddress,
    domContactNumber,
  });

  newProduct
    .save()
    .then(() => {
      res.json("Product added successfully");
    })
    .catch((err) => {
      console.log(err);
    });
});

//read Student details

router.route("/getdomproduct/").get((req, res) => {
  Product.find()
    .then((inventorys) => {
      res.json(inventorys);
    })
    .catch((err) => {
      console.log(err);
    });
});

//update product data
router.route("/domproductupdate/:id").put(async (req, res) => {
  let productId = req.params.id;
  const {
    domProductName,
    domProductCode,
    domProductQuentity,
    domExpireDate,
    domManDate,
    domShipingAddress,
    domContactNumber,
  } = req.body;

  const updateProduct = {
    domProductCode,
    domProductQuentity,
    domExpireDate,
    domManDate,
    domShipingAddress,
    domContactNumber,
  };
  const update = await Product.findByIdAndUpdate(productId, updateProduct)
    .then(() => {
      res.status(200).send({ status: "Dom Product updated" });
    })
    .catch((err) => {
      console.log(err);
      res
        .status(500)
        .send({ status: "error updating status data ", error: err.message });
    });
});

//delete the proct  data  from the  database

router.route("/domproductdelete/:id").delete(async (req, res) => {
  let productId = req.params.id;

  await Product.findByIdAndDelete(productId)
    .then(() => {
      res.status(200).send({ status: " Dom Product deleted" });
    })
    .catch((err) => {
      console
        .status(500)
        .send({ status: "Error deleting user", error: err.message });
    });
});

//single user data read

router.route("/domproductget/:id").get(async (req, res) => {
  let productId = req.params.id;
  await Product.findById(productId)
    .then((product) => {
      res.status(200).send({ status: " dom user fetchd", product });
    })
    .catch(() => {
      console.log(err.message);
      res
        .status(500)
        .send({ status: "error fetching  get user", error: err.message });
    });
});
module.exports = router;
