const router = require("express").Router();

const { response } = require("express");
let Product = require("../models/ExpProduct");

//add the new Product to databse
router.route("/expproductadd").post((req, res) => {
  const expProductName = req.body.expProductName;
  const expProductCode = Number(req.body.expProductCode);
  const expProductQuentity = Number(req.body.expProductQuentity);
  const expManDate = Date(req.body.expManDate);
  const expExpireDate = Date(req.body.expExpireDate);
  const expShipingAddress = req.body.expShipingAddress;
  const expContactNumber = req.body.expContactNumber;

  const newProduct = new Product({
    expProductName,
    expProductCode,
    expProductQuentity,
    expManDate,
    expExpireDate,
    expShipingAddress,
    expContactNumber,
  });

  newProduct
    .save()
    .then(() => {
      res.json(" Exp Product added successfully");
    })
    .catch((err) => {
      console.log(err);
    });
});

//read Student details

router.route("/getexproduct/").get((req, res) => {
  Product.find()
    .then((inventorys) => {
      res.json(inventorys);
    })
    .catch((err) => {
      console.log(err);
    });
});

//update product data
router.route("/expproductupdate/:id").put(async (req, res) => {
  let productId = req.params.id;
  const {
    expProductName,
    expProductCode,
    expProductQuentity,
    expManDate,
    expExpireDate,
    expShipingAddress,
    expContactNumber,
  } = req.body;

  const updateProduct = {
    expProductName,
    expProductCode,
    expProductQuentity,
    expManDate,
    expExpireDate,
    expShipingAddress,
    expContactNumber,
  };
  const update = await Product.findByIdAndUpdate(productId, updateProduct)
    .then(() => {
      res.status(200).send({ status: "Dom Product updated" });
    })
    .catch((err) => {
      console.log(err);
      res
        .status(500)
        .send({ status: "error updating status data ", error: err.message });
    });
});

//delete the proct  data  from the  database

router.route("/expproductdelete/:id").delete(async (req, res) => {
  let productId = req.params.id;

  await Product.findByIdAndDelete(productId)
    .then(() => {
      res.status(200).send({ status: " Dom Product deleted" });
    })
    .catch((err) => {
      console
        .status(500)
        .send({ status: "Error deleting user", error: err.message });
    });
});

//single user data read

router.route("/expproductget/:id").get(async (req, res) => {
  let productId = req.params.id;
  await Product.findById(productId)
    .then((product) => {
      res.status(200).send({ status: " dom user fetchd", product });
    })
    .catch(() => {
      console.log(err.message);
      res
        .status(500)
        .send({ status: "error fetching  get user", error: err.message });
    });
});
module.exports = router;
