const router = require("express").Router();

const { response } = require("express");

let Inventory = require("../models/Inventory");

//add the new inventory to databse
router.route("/add").post((req, res) => {
  const name = req.body.name;
  const age = Number(req.body.age);
  const gender = req.body.gender;
  const address = req.body.address;

  const newInventory = new Inventory({
    name,
    age,
    gender,
    address,
  });

  newInventory
    .save()
    .then(() => {
      res.json("Inventory added successfully");
    })
    .catch((err) => {
      console.log(err);
    });
});

//read Inventory details

router.route("/").get((req, res) => {
  Inventory.find()
    .then((students) => {
      res.json(students);
    })
    .catch((err) => {
      console.log(err);
    });
});

//update Inventory data
router.route("/updates/:id").put(async (req, res) => {
  let userId = req.params.id;
  const { name, age, gender, address } = req.body;

  const updateInventory = {
    name,
    age,
    gender,
    address,
  };
  const update = await Inventory.findByIdAndUpdate(userId, updateInventory)
    .then(() => {
      res.status(200).send({ status: "Inventory updated" });
    })
    .catch((err) => {
      console.log(err);
      res
        .status(500)
        .send({ status: "error updating status data ", error: err.message });
    });
});

//delete the inventory  data  from the  database

router.route("/idelete/:id").delete(async (req, res) => {
  let userId = req.params.id;

  await Inventory.findByIdAndDelete(userId)
    .then(() => {
      res.status(200).send({ status: "inventory deleted" });
    })
    .catch((err) => {
      console
        .status(500)
        .send({ status: "Error deleting user", error: err.message });
    });
});

//single user data read

router.route("/iget/:id").get(async (req, res) => {
  let userId = req.params.id;
  await Inventory.findById(userId)
    .then((inventory) => {
      res.status(200).send({ status: "inventory fetchd", inventory });
    })
    .catch(() => {
      console.log(err.message);
      res
        .status(500)
        .send({ status: "error fetching  get user", error: err.message });
    });
});

module.exports = router;
