const router = require("express").Router();

const { response } = require("express");
let Student = require("../models/Student");

//add the new student to databse
router.route("/add").post((req, res) => {
  const name = req.body.name;
  const age = Number(req.body.age);
  const gender = req.body.gender;
  const address = req.body.address;

  const newStudent = new Student({
    name,
    age,
    gender,
    address,
  });

  newStudent
    .save()
    .then(() => {
      res.json("student added successfully");
    })
    .catch((err) => {
      console.log(err);
    });
});

//read student details

router.route("/").get((req, res) => {
  Student.find()
    .then((inventorys) => {
      res.json(inventorys);
    })
    .catch((err) => {
      console.log(err);
    });
});

//update stuend data
router.route("/update/:id").put(async (req, res) => {
  let userId = req.params.id;
  const { name, age, gender, address } = req.body;

  const updateStudent = {
    name,
    age,
    gender,
    address,
  };
  const update = await Student.findByIdAndUpdate(userId, updateStudent)
    .then(() => {
      res.status(200).send({ status: "user updated" });
    })
    .catch((err) => {
      console.log(err);
      res
        .status(500)
        .send({ status: "error updating status data ", error: err.message });
    });
});

//delete the student  data  from the  database

router.route("/delete/:id").delete(async (req, res) => {
  let userId = req.params.id;

  await Student.findByIdAndDelete(userId)
    .then(() => {
      res.status(200).send({ status: "user deleted" });
    })
    .catch((err) => {
      console
        .status(500)
        .send({ status: "Error deleting user", error: err.message });
    });
});

//single user data read

router.route("/get/:id").get(async (req, res) => {
  let userId = req.params.id;
  await Student.findById(userId)
    .then((student) => {
      res.status(200).send({ status: "user fetchd", student });
    })
    .catch(() => {
      console.log(err.message);
      res
        .status(500)
        .send({ status: "error fetching  get user", error: err.message });
    });
});
module.exports = router;
